mapper.select:
SELECT

FROM sys_user a


    1=1
    AND a.id > 0
    AND a.del_flag = #{DEL_FLAG_NORMAL}
    AND a.is_wxuser = "0"




        ORDER BY ${order}


        ORDER BY a.update_date DESC


LIMIT #{limit} OFFSET #{offset}

SELECT

FROM sys_user a

left join user_tenant t on t.user_id = a.id

    1=1
    AND t.tenant_id = #{tenantId}
    AND t.del_flag = '0'
    AND a.id > 0
    AND a.del_flag = #{DEL_FLAG_NORMAL}
    AND a.is_wxuser = "0"




        ORDER BY ${order}


        ORDER BY a.update_date DESC


LIMIT #{limit} OFFSET #{offset}

SELECT
FROM sys_user a
1=1 AND a.id > 0 AND a.del_flag = #{DEL_FLAG_NORMAL}
ORDER BY ${order}
ORDER BY a.update_date DESC

