mapper.update:
UPDATE clinic SET
    update_by = #{updateBy},
    update_date = #{updateDate},
    del_flag = #{DEL_FLAG_DELETE}


        WHERE id = #{id}


        WHERE lessee_id = #{lesseeId.id}


