mapper.select:
SELECT 

FROM org_company a

WHERE a.id = #{id}

SELECT 

FROM org_company a


    1=1
    AND a.id > 0
    AND a.del_flag = #{DEL_FLAG_NORMAL}




        ORDER BY ${order}


        ORDER BY a.sort ASC


LIMIT #{limit} OFFSET #{offset}

SELECT

FROM org_company a


    1=1
    AND a.id > 0
    AND a.parent_id = 0
    AND a.del_flag = #{DEL_FLAG_NORMAL}




        ORDER BY ${order}


        ORDER BY a.sort ASC


LIMIT #{limit} OFFSET #{offset}

SELECT

FROM org_company a


    1=1
    AND a.id > 0
    AND a.parent_id != 0
    AND a.del_flag = #{DEL_FLAG_NORMAL}




        ORDER BY ${order}


        ORDER BY a.parent_id DESC


LIMIT #{limit} OFFSET #{offset}

SELECT
FROM org_company a
1=1 AND a.id > 0 AND a.del_flag = #{DEL_FLAG_NORMAL}
ORDER BY ${order}
ORDER BY a.sort ASC

SELECT
FROM org_company a
1=1 AND a.id > 0 AND a.parent_id = 0 AND a.del_flag = #{DEL_FLAG_NORMAL}
ORDER BY ${order}
ORDER BY a.sort ASC

SELECT
FROM org_company a
1=1 AND a.id > 0 AND a.parent_id != 0 AND a.del_flag = #{DEL_FLAG_NORMAL}
ORDER BY ${order}
ORDER BY a.sort ASC

mapper.update:
UPDATE org_company SET
    update_by = #{updateBy},
    update_date = #{updateDate},
    del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}


